import { Template } from 'meteor/templating';
import { ReactiveVar } from 'meteor/reactive-var';

import './main.html';


/*
var img_data = [
  {
    img_src:"laptops.png",
    img_alt:"some laptops"
  },
  {
    img_src:"ipad.jpg",
    img_alt:"some ipad"
  },
  {
    img_src:"mobile.png",
    img_alt:"some mobile"
  },
];*/

//Template.images.helpers({images: img_data});


Accounts.ui.config({
  passwordSignupFields: "USERNAME_AND_EMAIL"
});


Template.body.helpers({
  username:function(){
    if(Meteor.user()){
      return Meteor.user().emails[0].address;
    }
    else {
      return "Anonymous Internet User";
    }
  }
});


Template.images.helpers({images: 
  Img.find({}, {sort:{createdOn: -1, rating:-1}})
});


Template.images.events({
  'click .js-images': function(event){
    $(event.target).css("width","50px");
  },

  'click .js-del-images': function(event){
    var image_id = this._id;
    console.log(image_id);
    // use jquery to hide the image component
    // then remove it at the end of the animation
    $("#"+image_id).hide('slow', function(){
      Img.remove({"_id":image_id});
    })
  },

  'click .js-rate-image': function(event){
    var rating = $(event.currentTarget).data("userrating");
    console.log("You clicked  a star rate "+rating);
    var image_id = this.id;
    console.log("Image id is "+image_id);

    Img.update({_id:image_id},
                  {$set: {rating:rating}});
  },

  'click .js-show-image-form': function(event){
    $("#image_add_form").modal('show');

  }

});


Template.image_add_form.events({
  'submit .js-add-image': function(event){
    var img_src, img_alt;
    img_src = event.target.img_src.value;
    img_alt = event.target.img_alt.value;
    console.log("src :"+img_src+" alt:"+img_alt);

    Img.insert({
      img_src: img_src,
      img_alt: img_alt,
      createdOn: new Date()
    });

    $("#image_add_form").modal('hide');

    return false;
  }
});
